export const isPrivate = (job) => {
    return job.ops.some((op) => op.type === 'container/run' &&
        op.args.expose !== undefined &&
        op.args.private === true);
};
export const isExposed = (job) => {
    return job.ops.some((op) => op.type === 'container/run' &&
        op.args.expose !== undefined);
};
