import { promiseTimeoutWrapper } from '../../generic/timeoutPromiseWrapper.js';
export function getCotnainerInfo(container, timeout = 360, controller = new AbortController()) {
    return new Promise(async (resolve, reject) => {
        try {
            const info = await promiseTimeoutWrapper(container.inspect({
                abortSignal: controller.signal,
            }), timeout, controller);
            resolve(info);
        }
        catch (e) {
            reject(e);
        }
    });
}
