import EventEmitter from 'events';
export class StatusEmitter extends EventEmitter {
    close() {
        this.emit('close');
    }
    emitCreate(job_id, job) {
        this.emit('event', {
            event: 'create',
            job_id,
            job,
        });
    }
    emitStatus(job_id, node, status) {
        this.emit('event', {
            event: 'status',
            job_id,
            node,
            status,
        });
    }
    emitProgress(job_id, step) {
        this.emit('event', {
            event: 'progress',
            job_id,
            step,
        });
    }
}
